        receivers = [];
        let id = $(""#id"").val();
        let title = $(""#title"").val();
        let categories = ");
            WriteLiteral(@"$(""#categories"").val();
        let summary = $(""#summary"").val();
        let description = CKEDITOR.instances['description'].getData();

        $(""select[name^='user-']"").each(function (el) {
            try {
                let me = $(this).val();
                me.forEach(item => {
                    receivers.push(item);
                });
            } catch (e) {
                console.log(e);
            }
        });
        let obj = {""id"": id, ""title"": title, ""category"": categories, ""description"": summary, ""content"": description, ""receivers"": receivers }
        callApi(`${REP_URL}notifications?status=${stt}`, obj, ""POST"", function (item) {
            $.notify(""Thành công"", ""success"");
            $('#modalEdit').modal('hide');
        });
    }


    function public(id) {
        callApi(`${REP_URL}notifications/public/${id}`, null, ""POST"", function (item) {
            $.notify(""Thành công"", ""success"");
        });
    }

